class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      navHidden: false
    }
    this.handler = this.handler.bind(this)
  }

  handler() {
    this.setState({
      navHidden: !this.state.navHidden
    })
  }

  render() {
    return (
      <div className="App">
        <Header />
        <div 
          className={"app-left-menu " + (this.state.navHidden ? "app-left-menu-hidden" : "")}
        >
          <Logo navHidden={this.state.navHidden} handler ={this.handler} />
          <Nav navHidden={this.state.navHidden} />
        </div>
        <Main navHidden={this.state.navHidden} />
      </div>
    )
  }
}

